SELECT
	j.Id AS JournalKey,
	TO_CHAR(longtodatec(j.creation_time, 100), 'YYYY-MM-DD HH24-MI-SS') AS JournalCreationDateTime,
	TO_CHAR(longtodatec(s.creation_time, 100), 'YYYY-MM-DD HH24-MI-SS') AS SubscriptionCreationDateTime,
	pr.name AS ProductName,
	p.firstname AS FirstName,
	p.lastname AS LastName,
    s.state AS SubscriptionStateId
from subscriptions s
	join products pr
		on s.subscriptiontype_center = pr.center
		and s.subscriptiontype_id = pr.id
	join persons p
		on p.center = s.owner_center
		and p.id = s.owner_id
	join journalentries j
		on s.owner_center = j.person_center
		and s.owner_id = j.person_id
        and TO_CHAR(longtodatec(j.creation_time, 100), 'YYYY-MM-DD HH24-MI') = TO_CHAR(longtodatec(s.creation_time, 100), 'YYYY-MM-DD HH24-MI')
