SELECT "source"."center_id" AS "center_id", "source"."Person FullID" AS "Person FullID", "source"."home_center_id" AS "home_center_id", "source"."check_in_datetime" AS "check_in_datetime", "source"."check_out_datetime" AS "check_out_datetime", "source"."result" AS "result", "source"."card_checked_in" AS "card_checked_in", "source"."Center__shortname" AS "Center__shortname", "source"."Center__country_code" AS "Center__country_code", "source"."Person Detail__full_name" AS "Person Detail__full_name", "source"."Person__person_type" AS "Person__person_type"
FROM (SELECT "Center"."country_code" AS "Center__country_code", "visit_log"."check_in_datetime" AS "check_in_datetime", "Center"."shortname" AS "Center__shortname", "Person"."home_center_id" AS "Person__home_center_id", "Person"."home_center_person_id" AS "Person__home_center_person_id", "visit_log"."center_id" AS "center_id", concat(concat("Person"."home_center_id", 'p'), "Person"."home_center_person_id") AS "Person FullID", "visit_log"."home_center_id" AS "home_center_id", "visit_log"."check_out_datetime" AS "check_out_datetime", "visit_log"."result" AS "result", "visit_log"."card_checked_in" AS "card_checked_in", "Person Detail"."full_name" AS "Person Detail__full_name", "Person"."person_type" AS "Person__person_type" FROM "visit_log"
LEFT JOIN "center" "Center" ON "visit_log"."center_id" = "Center"."id" LEFT JOIN "person_detail" "Person Detail" ON "visit_log"."person_id" = "Person Detail"."person_id" LEFT JOIN "person" "Person" ON "visit_log"."person_id" = "Person"."id") "source"
WHERE ("source"."Center__country_code" = 'CH'
   AND CAST("source"."check_in_datetime" AS date) = CAST(getdate() AS date) AND "source"."Center__shortname" = 'ZUR')