EXISTS ( SELECT CHECKINS.PERSON_CENTER, CHECKINS.PERSON_ID, COUNT(*) as NB FROM CHECKINS WHERE CHECKINS.PERSON_CENTER = P.CENTER and CHECKINS.PERSON_ID = P.ID and CHECKIN_TIME BETWEEN :Check_in_from_date AND  (:Check_in_To_date + 86400 * 1000 - 1) GROUP BY CHECKINS.PERSON_CENTER, CHECKINS.PERSON_ID HAVING COUNT(*) BETWEEN :min AND :max )
