SELECT ((br.center || 'br'::text) || br.id) AS "RESOURCE_ID", br.name AS "NAME", br.state AS "STATE", br.type AS "TYPE", (bpg.id)::character varying(255) AS "ACCESS_GROUP_ID", bpg.name AS "ACCESS_GROUP_NAME", br.external_id AS "EXTERNAL_ID", (br.center)::character varying(255) AS "RESOURCE_CENTER_ID", br.coment AS "COMMENT", CASE br.show_calendar WHEN 1 THEN 'TRUE'::text ELSE 'FALSE'::text END AS "SHOW_CALENDAR", (br.center)::character varying(255) AS "CENTER_ID", br.last_modified AS "ETS" FROM (booking_resources br LEFT JOIN booking_privilege_groups bpg ON ((bpg.id = br.attend_privilege_id))) 

