SELECT (ps.id)::character varying(255) AS "ID", ps.name AS "NAME", ps.description AS "DESCRIPTION", ps.privilege_set_groups_id AS "PRIVILEGE_SET_GROUP_ID", psg.name AS "PRIVILEGE_SET_GROUP_NAME", CASE WHEN ((ps.scope_type)::text = 'C'::text) THEN 'CENTER'::text WHEN ((ps.scope_type)::text = 'A'::text) THEN 'AREA'::text WHEN ((ps.scope_type)::text = 'T'::text) THEN 'GLOBAL'::text ELSE NULL::text END AS "SCOPE_TYPE", ps.scope_id AS "SCOPE_ID" FROM (privilege_sets ps LEFT JOIN privilege_set_groups psg ON ((ps.privilege_set_groups_id = psg.id))) 