SELECT (ag.id)::character varying(255) AS "ACTIVITY_GROUP_ID", ag.name AS "NAME", ag.state AS "STATE", CASE WHEN (ag.bookable_in_kiosk = 1) THEN 'TRUE'::text ELSE 'FALSE'::text END AS "BOOK_KIOSK", CASE WHEN (ag.bookable_on_web = 1) THEN 'TRUE'::text ELSE 'FALSE'::text END AS "BOOK_WEB", CASE WHEN (ag.bookable_via_api = 1) THEN 'TRUE'::text ELSE 'FALSE'::text END AS "BOOK_API", CASE WHEN (ag.bookable_via_mobile_api = 1) THEN 'TRUE'::text ELSE 'FALSE'::text END AS "BOOK_MOBILE_API", CASE WHEN (ag.bookable_on_frontdesk_app = 1) THEN 'TRUE'::text ELSE 'FALSE'::text END AS "BOOK_CLIENT", ag.parent_activity_group_id AS "PARENT_ACTIVITY_GROUP_ID", ag.external_id AS "EXTERNAL_ID", ag.last_modified AS "ETS" FROM activity_group ag WHERE ((ag.top_node_id IS NULL) AND ((ag.state)::text <> 'DRAFT'::text)) 