SELECT p.center || 'p' || p.id AS "PERSONKEY", ar.balance FROM account_receivables ar JOIN persons p ON p.center = ar.customercenter AND p.id = ar.customerid JOIN payment_accounts pac ON pac.center = ar.center AND pac.id = ar.id JOIN payment_agreements pag ON pag.center = pac.active_agr_center AND pag.id = pac.active_agr_id AND pag.subid = pac.active_agr_subid WHERE ar.balance < 0 AND ar.ar_type = 4 AND p.sex != 'C' AND p.center IN (:Scope) 